    /**
     * Busca un producto por su codigo y lo introduce en pantalla
     * @param evt 
     */
    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        // TODO add your handling code here:
        String strCodigo=this.txtBuscar.getText();
        if (strCodigo.length()!=0) {
            try {
                int codigo=Integer.parseInt(strCodigo);
                //buscar y mostrar el producto
            } catch (java.lang.NumberFormatException ex) {
                JOptionPane.showMessageDialog(null,"El formato del código no es un número");
            }
        } else {
            JOptionPane.showMessageDialog(null,"El código está vacío");
        }
    }//GEN-LAST:event_btnBuscarActionPerformed

